import cv2oldValueFace = 0oldValueEye = 0counter = 0faceCascade = cv2.CascadeClassifier('haarcascade_frontalface_default.xml')video_capture = cv2.VideoCapture(0)while True:    ret, frame = video_capture.read()    gray = cv2.cvtColor(frame, cv2.COLOR_BGR2GRAY)    faces = faceCascade.detectMultiScale(        gray,        scaleFactor=1.1,        minNeighbors=5,        minSize=(30, 30),        flags=cv2.CASCADE_SCALE_IMAGE    )    # Draw a rectangle around the faces    for (x, y, w, h) in faces:        cv2.rectangle(frame, (x-2, y-2), (x+w+1, y+h+1), (0, 255, 0), 2)        # cv2.rectangle(frame, (x+(w/2)-1, y+(h/2)-1), (x+(w/2)+1, y+(h/2)+1), (0, 0, 255), 2)        face = frame[y:y+h,x:x+w]        cv2.imwrite('/tmp/testing/'+ str(counter) + '.png', face)        roi_gray = gray[y:y+h, x:x+w]        roi_color = frame[y:y+h, x:x+w]        counter = counter +1    # Display the resulting frame    cv2.imshow('Video', frame)    if cv2.waitKey(1) & 0xFF == ord('q'):        break# When everything is done, release the capturevideo_capture.release()cv2.destroyAllWindows()